import React,{Component} from 'react';
import Button from '../../../components/UI/Button/Button';
import './ContactData.css';
import axios from '../../../axios-orders';
import Spinner from '../../../components/UI/Spinner/Spinner';
import Input from '../../../components/UI/Input/Input';

class ContactData extends Component {

    state={
        orderForm:{
            
                name:{ elementType:'input',
                elementconfig:{
                    type:'text',
                    placeholder:'Your Name'
                },
                value:''
                },
                street:{ elementType:'input',
                elementconfig:{
                    type:'text',
                    placeholder:'Street'
                },
                value:''
                },
                zipCode:{ elementType:'input',
                elementconfig:{
                    type:'text',
                    placeholder:'Pincode'
                },
                value:''
                },
                country:{ elementType:'input',
                elementconfig:{
                    type:'text',
                    placeholder:'Country'
                },
                value:''
                },
                email:{ elementType:'input',
                elementconfig:{
                    type:'email',
                    placeholder:'Your email'
                },
                value:''
                },
                deliveryMethod :{ elementType:'select',
                elementconfig:{
                    type:'select',
                    options:[
                        {value:'fastest',displayValue:'Fastest'},
                    {value:'cheapest',displayValue:'Cheapest'}
                ]
                },
                value:''
                }
            },
        
        // name:'',
        // email:'',
        // address :{
        //     street :'',
        //     postal_code:''
        // },
        loading:false
    }

    oderderHandler=(event)=>{
        event.preventDefault();
        console.log(this.props.ingredients);
          this.setState({loading:true});
                        const order={
                            ingredients:this.props.ingredients,
                            price:this.props.price,
                           
                            
                        }

                        axios.post('/orders.json',order)
                        .then(response => {
                            this.setState({loading:false});
                            console.log(response);
                            this.props.history.push('/');
                        })
                        .catch(err=>{
                            console.log(err);
                            this.setState({loading:false});
                        } );
    }
    render(){

const formElementsArray=[];
for(let key in this.state.orderForm){
    formElementsArray.push({
        id:key,
        config:this.state.orderForm[key],
    
    })
}




        let form=( <form>
            {/* <Input elementType="..." elementconfig ="..." value="..." /> */}
            {formElementsArray.map(formElement=>(
                <Input key={formElement.id} elementType={formElement.config.elementType}
                elementconfig={formElement.config.elementconfig}
                value={formElement.config.value} />
            ))}
         
          
<Button btnType="Success" clicked={this.oderderHandler}>Order</Button>
        </form>);
        if(this.state.loading){
            form=<Spinner />;
        }
        return (
            <div className="ContactData">
                <h4>Enter Your ContactData</h4>
               {form}
            </div>

        );
    }
}


export default ContactData;
