import React,{Component} from 'react';
import Button from '../../../components/UI/Button/Button';
import './ContactData.css';
import axios from '../../../axios-orders';
import Spinner from '../../../components/UI/Spinner/Spinner';
import Input from '../../../components/UI/Input/Input';

class ContactData extends Component {

    state={
        orderForm:{
            
                name:{ elementType:'input',
                elementConfig:{
                    type:'text',
                    placeholder:'Your Name'
                },
                value:'',
                validation:{
                    required:true
                },valid:false
                },
                street:{ elementType:'input',
                elementConfig:{
                    type:'text',
                    placeholder:'Street'
                },
                value:'',
                validation:{
                    required:true
                },valid:false
                },
                zipCode:{ elementType:'input',
                elementConfig:{
                    type:'text',
                    placeholder:'Pincode'
                },
                value:'',
                validation:{
                    required:true
                },valid:false
                },
                country:{ elementType:'input',
                elementConfig:{
                    type:'text',
                    placeholder:'Country'
                },
                value:'',
                validation:{
                    required:true
                },valid:false
                },
                email:{ elementType:'input',
                elementConfig:{
                    type:'email',
                    placeholder:'Your email'
                },
                value:'',
                validation:{
                    required:true
                },valid:false
                },
                deliveryMethod :{
                elementType:'select',
                elementConfig:{
                    options:[
                        {value:'fastest',displayValue:'Fastest'},
                    {value:'cheapest',displayValue:'Cheapest'}
                ]
                },
                value:''
                }
            },
        
        // name:'',
        // email:'',
        // address :{
        //     street :'',
        //     postal_code:''
        // },
        loading:false
    }

    oderderHandler=(event)=>{
        event.preventDefault();
        console.log(this.props.ingredients);
          this.setState({loading:true});
const formData={};
for(let formElementIdentifier in this.state.orderForm){
    formData[formElementIdentifier]= this.state.orderForm[formElementIdentifier].value;
}

                        const order={
                            ingredients:this.props.ingredients,
                            price:this.props.price,
                           orderData:formData
                            
                        }

                        axios.post('/orders.json',order)
                        .then(response => {
                            this.setState({loading:false});
                            console.log(response);
                            this.props.history.push('/');
                        })
                        .catch(err=>{
                            console.log(err);
                            this.setState({loading:false});
                        } );
    }



    checkValidity(value,rules){
        let isValid=false;
if(rules.required){
    isValid =value.trim() !== '';

}
return isValid;    
}

inputChangedHandler=(event,inputIdentifier)=>{
//console.log(event.target.value);
const updatedOrderForm={
    ...this.state.orderForm
}

const updatedFormELement={...updatedOrderForm[inputIdentifier]};
updatedFormELement.value=event.target.value;
updatedFormELement.valid=this.checkValidity(updatedFormELement.value,updatedFormELement.validation);

updatedOrderForm[inputIdentifier]=updatedFormELement;
console.log(updatedFormELement);
this.setState({orderForm:updatedOrderForm});


}

    render(){

const formElementsArray=[];
for(let key in this.state.orderForm){
    formElementsArray.push({
        id:key,
        config:this.state.orderForm[key],
    
    })
}




        let form=( <form onSubmit={this.oderderHandler}>
            {/* <Input elementType="..." elementconfig ="..." value="..." /> */}
         
            {formElementsArray.map(formElement=>(
                <Input key={formElement.id} elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value={formElement.config.value}
                changed={(event)=>this.inputChangedHandler(event,formElement.id)} />
            ))}
         
          
<Button btnType="Success" clicked={this.oderderHandler}>Order</Button>
        </form>);
        if(this.state.loading){
            form=<Spinner />;
        }
        return (
            <div className="ContactData">
                <h4>Enter Your ContactData</h4>
               {form}
            </div>

        );
    }
}


export default ContactData;
