import React from 'react';
import './Input.css';

const input=(props)=>{
let inputElement=null;

switch(props.inputtype){
    case ('input'):
        inputElement =<input className="InputElement" 
        {...props.elementconfig} 
        value={props.value}/>;
        break
        case('textarea'):
        inputElement =<textarea className="InputElement" 
        {...props.elementconfig} 
        value={props.value}/>;
        break
        case('select'):
        inputElement =<select className="InputElement" 
        {...props.elementconfig} 
        value={props.value}>
          {props.elementconfig.options.map(option =>(
              <option value={option.value}>{option.displayValue}</option>
          ))}
        </select>;
        break
        default:
            inputElement=<input className="InputElement" 
            {...props.elementconfig} 
            value={props.value}/>;

}

return(
    <div className="Input">
    <label className="Label"> {props.label}</label>
    {inputElement}
</div>
);

};



export default input;
